CREATE TABLE categoria(
    nome VARCHAR(20) NOT NULL,
    CONSTRAINT pk_categoria PRIMARY KEY(nome)
);


CREATE TABLE categoria_simples(
    nome VARCHAR(20) NOT NULL,
    CONSTRAINT pk_categoria_simples PRIMARY KEY (nome),
    CONSTRAINT fk_categoriasimples_categoria FOREIGN KEY (nome) REFERENCES categoria(nome)
);


CREATE TABLE super_categoria(
    nome VARCHAR(20) NOT NULL,
    CONSTRAINT pk_super_categoria PRIMARY KEY (nome),
    CONSTRAINT fk_supercategoria_categoria FOREIGN KEY (nome) REFERENCES categoria(nome)
);



CREATE TABLE tem_outra(
    nome_super VARCHAR(20) NOT NULL,
    nome_sub VARCHAR(20) NOT NULL,
    CONSTRAINT pk_tem_outra PRIMARY KEY (nome_sub),
    CONSTRAINT fk_tem_outra_categoria FOREIGN KEY (nome_sub) REFERENCES categoria(nome),
    CONSTRAINT fk_tem_outra_supercategoria FOREIGN KEY (nome_super) REFERENCES super_categoria(nome)
);

CREATE TABLE produto(
    ean INT NOT NULL,
    cat VARCHAR(20) NOT NULL,
    descr VARCHAR(20) NOT NULL,
    CONSTRAINT pk_produto PRIMARY KEY (ean),
    CONSTRAINT fk_produto_categoria FOREIGN KEY (cat) REFERENCES categoria(nome)
);


CREATE TABLE tem_categoria(
    ean INT NOT NULL,
    nome VARCHAR(20) NOT NULL,
    CONSTRAINT fk_temCategoria_produto FOREIGN KEY (ean) REFERENCES produto(ean),
    CONSTRAINT fk_temCategoria_categoria FOREIGN KEY (nome) REFERENCES categoria(nome) 
);

CREATE TABLE ivm(
    num_serie INT NOT NULL,
    fabricante VARCHAR(20) NOT NULL,
    CONSTRAINT pk_ivm PRIMARY KEY (num_serie, fabricante)
);

CREATE TABLE ponto_de_retalho(
    nome VARCHAR(30) NOT NULL,
    distrito VARCHAR(20) NOT NULL,
    concelho VARCHAR(20) NOT NULL,
    CONSTRAINT pk_ponto_de_retalho PRIMARY KEY (nome)
);

CREATE TABLE instalada_em(
    num_serie INT NOT NULL,
    fabricante VARCHAR(20) NOT NULL,
    "local" VARCHAR(30) NOT NULL,
    CONSTRAINT pk_instalada_em PRIMARY KEY (num_serie, fabricante),
    CONSTRAINT fk_instaladaEm_ivm FOREIGN KEY (num_serie, fabricante) REFERENCES ivm(num_serie, fabricante),
    CONSTRAINT fk_instaladaEm_pontoDeRetalho FOREIGN KEY ("local") REFERENCES ponto_de_retalho(nome)
);

CREATE TABLE prateleira(
    nro INT NOT NULL,
    num_serie INT NOT NULL,
    fabricante VARCHAR(20) NOT NULL,
    altura FLOAT NOT NULL,
    nome VARCHAR(20) NOT NULL,
    CONSTRAINT pk_prateleira PRIMARY KEY (nro, num_serie, fabricante),
    CONSTRAINT fk_prateleira_ivm_ns FOREIGN KEY (num_serie, fabricante) REFERENCES ivm(num_serie, fabricante),
    CONSTRAINT fk_prateleira_categoria FOREIGN KEY (nome) REFERENCES categoria(nome)
);

CREATE TABLE planograma(
    ean int NOT NULL,
    nro INT NOT NULL,
    num_serie INT NOT NULL,
    fabricante VARCHAR(20) NOT NULL,
    faces INT NOT NULL,
    unidades INT NOT NULL,
    loc VARCHAR(50) NOT NULL,
    CONSTRAINT pk_planograma PRIMARY KEY (ean, nro, num_serie, fabricante),
    CONSTRAINT fk_planograma_produto FOREIGN KEY (ean) REFERENCES produto(ean),
    CONSTRAINT fk_planograma_prateleira FOREIGN KEY (nro, num_serie, fabricante) REFERENCES prateleira(nro, num_serie, fabricante)
);

CREATE TABLE retalhista(
    tin VARCHAR(10) NOT NULL,
    nome VARCHAR(30) NOT NULL UNIQUE,
    CONSTRAINT pk_retalhista PRIMARY KEY (tin)
);

CREATE TABLE responsavel_por(
    nome_cat VARCHAR(20) NOT NULL,
    tin VARCHAR(10) NOT NULL,
    num_serie INT NOT NULL,
    fabricante VARCHAR(20) NOT NULL,
    CONSTRAINT pk_responsavel_por PRIMARY KEY (num_serie, fabricante),
    CONSTRAINT fk_responsavelPor_retalhista FOREIGN KEY (tin) REFERENCES retalhista(tin),
    CONSTRAINT fk_responsavelPor_categoria FOREIGN KEY (nome_cat) REFERENCES categoria(nome),
    CONSTRAINT fk_responsavelPor_ivm_ns FOREIGN KEY (num_serie, fabricante) REFERENCES ivm(num_serie, fabricante)
);

CREATE TABLE evento_reposicao(
    ean INT NOT NULL,
    nro INT NOT NULL,
    num_serie INT NOT NULL,
    fabricante VARCHAR(20) NOT NULL,
    instante TIMESTAMP,
    unidades INT NOT NULL, 
    tin VARCHAR(10) NOT NULL,
    CONSTRAINT pk_evento_reposicao PRIMARY KEY(ean, nro, num_serie, fabricante, instante),
    CONSTRAINT fk_eventoReposicao_planograma_ean FOREIGN KEY (ean, nro, num_serie, fabricante) REFERENCES planograma(ean, nro, num_serie, fabricante),
    CONSTRAINT fk_eventoReposicao_retalhista FOREIGN KEY (tin) REFERENCES retalhista(tin)
);


